import React, { useState } from 'react';
import { useIdeas } from '../../contexts/IdeasContext';
import { useAuth } from '../../contexts/AuthContext';
import { IdeaStatus, Idea } from '../../types';
import { ChevronRight, User, Calendar, MessageSquare, Edit2, X, Save, Plus, UserPlus, Tag } from 'lucide-react';
import { format } from 'date-fns';
import { ptBR } from 'date-fns/locale';

export const KanbanBoard: React.FC = () => {
  const { ideas, updateIdeaStatus } = useIdeas();
  const { user } = useAuth();
  const [selectedIdea, setSelectedIdea] = useState<string | null>(null);
  const [editingCard, setEditingCard] = useState<string | null>(null);
  const [cardData, setCardData] = useState<{
    title: string;
    description: string;
    assignee: string;
    tags: string[];
    dueDate: string;
  }>({
    title: '',
    description: '',
    assignee: '',
    tags: [],
    dueDate: ''
  });

  const columns = [
    { status: IdeaStatus.RECEIVED, title: 'Recebidas', color: 'bg-gray-100' },
    { status: IdeaStatus.ANALYZING, title: 'Em Análise', color: 'bg-blue-100' },
    { status: IdeaStatus.APPROVED, title: 'Aprovadas', color: 'bg-green-100' },
    { status: IdeaStatus.IN_PROGRESS, title: 'Em Implementação', color: 'bg-yellow-100' },
    { status: IdeaStatus.IMPLEMENTED, title: 'Concluídas', color: 'bg-purple-100' }
  ];

  const handleDragStart = (e: React.DragEvent, ideaId: string) => {
    e.dataTransfer.setData('ideaId', ideaId);
  };

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
  };

  const handleDrop = (e: React.DragEvent, status: IdeaStatus) => {
    e.preventDefault();
    const ideaId = e.dataTransfer.getData('ideaId');
    updateIdeaStatus(ideaId, status);
  };

  const getIdeasByStatus = (status: IdeaStatus) => {
    return ideas.filter(idea => idea.status === status);
  };

  const handleEditCard = (idea: Idea) => {
    setEditingCard(idea.id);
    setCardData({
      title: idea.title,
      description: idea.description,
      assignee: idea.assignee || '',
      tags: idea.tags || [],
      dueDate: idea.dueDate || ''
    });
  };

  const handleSaveCard = () => {
    if (editingCard) {
      // Aqui você salvaria as alterações no contexto
      // updateIdea(editingCard, cardData);
      setEditingCard(null);
    }
  };

  const handleAddTag = (tag: string) => {
    if (tag && !cardData.tags.includes(tag)) {
      setCardData(prev => ({
        ...prev,
        tags: [...prev.tags, tag]
      }));
    }
  };

  const handleRemoveTag = (tagToRemove: string) => {
    setCardData(prev => ({
      ...prev,
      tags: prev.tags.filter(tag => tag !== tagToRemove)
    }));
  };

  return (
    <div className="bg-white rounded-lg shadow-md p-6">
      <div className="mb-6">
        <h2 className="text-2xl font-bold text-gray-900">Sistema de Inovação - Painel Administrativo</h2>
        <div className="mt-2 flex items-center space-x-6 text-sm text-gray-600">
          <span>Total de ideias: {ideas.length}</span>
          <span>Em análise: {getIdeasByStatus(IdeaStatus.ANALYZING).length}</span>
          <span>Implementadas: {getIdeasByStatus(IdeaStatus.IMPLEMENTED).length}</span>
        </div>
      </div>

      <div className="grid grid-cols-5 gap-4">
        {columns.map(column => (
          <div
            key={column.status}
            className={`${column.color} rounded-lg p-4 min-h-[500px]`}
            onDragOver={handleDragOver}
            onDrop={(e) => handleDrop(e, column.status)}
          >
            <h3 className="font-semibold text-gray-900 mb-4">{column.title}</h3>
            <div className="space-y-3">
              {getIdeasByStatus(column.status).map(idea => (
                <div
                  key={idea.id}
                  draggable
                  onDragStart={(e) => handleDragStart(e, idea.id)}
                  className="bg-white rounded-lg p-3 shadow cursor-move hover:shadow-md transition-shadow"
                  onClick={() => setSelectedIdea(idea.id)}
                >
                  <h4 className="font-medium text-gray-900 text-sm mb-2">{idea.title}</h4>

                  <div className="flex items-center justify-between text-xs text-gray-500">
                    <div className="flex items-center space-x-1">
                      <User className="w-3 h-3" />
                      <span>{idea.author?.name}</span>
                    </div>
                    <div className="flex items-center space-x-1">
                      <Calendar className="w-3 h-3" />
                      <span>{format(new Date(idea.createdAt), 'dd/MM', { locale: ptBR })}</span>
                    </div>
                  </div>

                  <div className="mt-2 flex items-center justify-between">
                    <div className="flex items-center space-x-2 text-xs">
                      <span className="bg-gray-200 px-2 py-1 rounded">{idea.votes} votos</span>
                      <span className="bg-gray-200 px-2 py-1 rounded">{idea.comments.length} comentários</span>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>

      {selectedIdea && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg p-6 max-w-2xl w-full max-h-[80vh] overflow-y-auto">
            <div className="flex justify-between items-start mb-4">
              <h3 className="text-xl font-semibold">Detalhes da Ideia</h3>
              <button
                onClick={() => setSelectedIdea(null)}
                className="text-gray-500 hover:text-gray-700"
              >
                ✕
              </button>
            </div>

            {/* Conteúdo do modal com detalhes da ideia */}
            <div className="space-y-4">
              {/* Implementar formulário de feedback e atribuição */}
              <button
                onClick={() => setSelectedIdea(null)}
                className="btn-primary"
              >
                Salvar Alterações
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};
