import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { useIdeas } from '../../contexts/IdeasContext';
import { useAI } from '../../contexts/AIContext';
import { categories } from '../../data/mockData';
import { IdeaFormData } from '../../types';
import { 
  Lightbulb, 
  AlertCircle, 
  Sparkles, 
  Loader2, 
  ChevronDown, 
  ChevronUp,
  Save,
  Send,
  RefreshCw
} from 'lucide-react';

export const EnhancedIdeaForm: React.FC = () => {
  const navigate = useNavigate();
  const { submitIdea, ideas } = useIdeas();
  const { analyzeIdea, configuration } = useAI();
  
  const [formData, setFormData] = useState<Partial<IdeaFormData>>({
    title: '',
    category: '',
    what: '',
    who: '',
    when: '',
    where: '',
    why: '',
    how: '',
    howMuch: '',
    difficulties: '',
    resources: '',
    attentionPoints: '',
    description: '',
    benefits: ''
  });
  const [similarIdeas, setSimilarIdeas] = useState<string[]>([]);
  const [showAIPanel, setShowAIPanel] = useState(false);
  const [aiSuggestions, setAiSuggestions] = useState<any>(null);
  const [expandedSections, setExpandedSections] = useState<Set<string>>(new Set(['basic']));
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [lastAnalyzedData, setLastAnalyzedData] = useState<string>('');

  // Estados para análise campo a campo
  const [fieldSuggestions, setFieldSuggestions] = useState<Record<string, any>>({});
  const [fieldTimers, setFieldTimers] = useState<Record<string, NodeJS.Timeout>>({});  const [analyzingFields, setAnalyzingFields] = useState<Set<string>>(new Set());
  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  const [fieldStatus, setFieldStatus] = useState<Record<string, 'analyzing' | 'suggestion' | 'approved' | 'praised'>>({});
  // Função para gerar sugestões inteligentes baseadas no conteúdo
  const generateSmartSuggestion = (fieldName: string, currentValue: string): string => {
    const value = currentValue.toLowerCase();
    
    // Sugestões específicas e inteligentes baseadas no conteúdo
    switch (fieldName) {
      case 'title':
        if (value.includes('papel') || value.includes('higiene')) {
          return `${currentValue} - Proposta de otimização para redução de custos e melhoria da qualidade`;
        }
        if (value.includes('sistema') || value.includes('software')) {
          return `${currentValue}: Sistema inovador para modernização dos processos da Proeng`;
        }
        return `${currentValue} - Inovação estratégica para crescimento da Proeng`;
        
      case 'description':
        if (value.includes('papel') || value.includes('higiene')) {
          return `${currentValue}\n\nEsta proposta visa otimizar os custos operacionais da empresa através da substituição por materiais de melhor qualidade-preço. Baseado em observação prática do consumo diário, a mudança pode resultar em economia significativa sem comprometer o bem-estar dos colaboradores.`;
        }
        if (value.includes('processo') || value.includes('melhoria')) {
          return `${currentValue}\n\nEsta inovação propõe modernizar nossos processos atuais, aumentando a eficiência operacional e reduzindo retrabalhos. A implementação gradual permitirá adaptação da equipe e mensuração dos resultados de forma controlada.`;
        }
        return `${currentValue}\n\nEsta ideia representa uma oportunidade de inovação alinhada com os objetivos estratégicos da Proeng, contribuindo para o crescimento sustentável da empresa e melhoria contínua de nossos processos.`;
        
      case 'benefits':
        if (value.includes('economia') || value.includes('custo')) {
          return `${currentValue}\n\n• Redução de custos operacionais mensais\n• Melhoria da sustentabilidade financeira\n• Otimização do orçamento para outros investimentos estratégicos\n• Maior competitividade no mercado`;
        }
        return `${currentValue}\n\n• Aumento da eficiência operacional\n• Melhoria do ambiente de trabalho\n• Fortalecimento da imagem da Proeng como empresa inovadora\n• Maior satisfação dos colaboradores`;
        
      case 'what':
        if (value.includes('papel') || value.includes('higiene')) {
          return `Substituição da marca atual de papel higiênico por uma opção de melhor qualidade que reduz o consumo devido à maior resistência e absorção. A proposta inclui teste piloto, análise de custo-benefício e implementação gradual em todas as unidades.`;
        }
        return `${currentValue} - Implementação de solução inovadora que moderniza processos atuais, otimiza recursos e melhora resultados operacionais da Proeng.`;
        
      case 'who':
        return `Equipe de implementação: funcionários da área afetada, gestores responsáveis, equipe de compras/suprimentos da Proeng, e colaboradores que utilizam diariamente o recurso para validação prática.`;
        
      case 'when':
        return `Cronograma sugerido: Fase 1 (30 dias) - Pesquisa e cotações; Fase 2 (15 dias) - Teste piloto; Fase 3 (15 dias) - Avaliação de resultados; Fase 4 (30 dias) - Implementação completa caso aprovado.`;
        
      case 'where':
        return `Implementação inicial: Escritório principal da Proeng para teste piloto. Após validação positiva, expansão gradual para todas as unidades e setores da empresa.`;
        
      case 'why':
        if (value.includes('economia') || value.includes('custo')) {
          return `${currentValue}\n\nJustificativa: Observação prática demonstra consumo excessivo com a marca atual devido à baixa qualidade, gerando desperdício e aumentando custos. A mudança estratégica alinha economia com sustentabilidade e bem-estar dos colaboradores.`;
        }
        return `${currentValue}\n\nEsta necessidade surge da busca contínua por melhorias operacionais que fortaleçam a posição competitiva da Proeng no mercado, otimizando recursos e promovendo inovação constante.`;
        
      case 'how':
        return `Metodologia de implementação: 1) Pesquisa de mercado e fornecedores; 2) Análise técnica e financeira; 3) Teste controlado com amostra representativa; 4) Coleta de feedback dos usuários; 5) Análise de resultados; 6) Decisão baseada em dados; 7) Implementação total se aprovado.`;
        
      case 'howMuch':
        return `Investimento estimado: Custo inicial de pesquisa e teste piloto (baixo). Investimento principal será na aquisição do novo produto, com expectativa de retorno através da redução de consumo e economia mensal. ROI esperado em 3-6 meses.`;
        
      default:
        return `${currentValue} - Detalhamento adicional alinhado com os objetivos da Proeng.`;
    }
  };

  // Função para analisar um campo específico
  const analyzeField = useCallback(async (fieldName: string, value: string) => {
    if (!configuration?.isActive || !value.trim() || value.length < 5) {
      return;
    }

    setAnalyzingFields(prev => new Set(Array.from(prev).concat(fieldName)));
    setFieldStatus(prev => ({ ...prev, [fieldName]: 'analyzing' }));

    try {
      // Simular análise de IA com sugestões inteligentes
      const mockAnalysis = await new Promise<{suggestion?: string, praise?: string}>((resolve) => {
        setTimeout(() => {
          // Determinar se precisa de sugestão baseado no tamanho e qualidade do conteúdo
          const needsSuggestion = value.length < 50 || !value.includes('.');
          
          if (needsSuggestion) {
            const smartSuggestion = generateSmartSuggestion(fieldName, value);
            resolve({ suggestion: smartSuggestion });
          } else {
            const praises = [
              'Excelente! Sua descrição está muito clara e completa.',
              'Ótimo trabalho! Informação bem estruturada e relevante.',
              'Perfeito! Este conteúdo está alinhado com nossos objetivos.',
              'Muito bom! Descrição objetiva e bem fundamentada.',
              'Excelente contribuição! Sua ideia está bem desenvolvida.'
            ];
            
            resolve({
              praise: praises[Math.floor(Math.random() * praises.length)]
            });
          }
        }, 1000);
      });

      setFieldSuggestions(prev => ({
        ...prev,
        [fieldName]: mockAnalysis
      }));

      setFieldStatus(prev => ({
        ...prev,
        [fieldName]: mockAnalysis.suggestion ? 'suggestion' : 'praised'
      }));

    } catch (error) {
      console.error('Erro na análise do campo:', error);
    } finally {
      setAnalyzingFields(prev => {
        const newSet = new Set(prev);
        newSet.delete(fieldName);
        return newSet;
      });
    }
  }, [configuration]);

  // Função para aplicar sugestão de campo
  const applyFieldSuggestion = (fieldName: string, suggestion: string) => {
    setFormData(prev => ({ ...prev, [fieldName]: suggestion }));
    setFieldStatus(prev => ({ ...prev, [fieldName]: 'approved' }));
    // Limpar a sugestão após aplicar
    setFieldSuggestions(prev => {
      const newSuggestions = { ...prev };
      delete newSuggestions[fieldName];
      return newSuggestions;
    });
  };

  // Função para dispensar sugestão
  const dismissFieldSuggestion = (fieldName: string) => {
    setFieldStatus(prev => ({ ...prev, [fieldName]: 'approved' }));
    setFieldSuggestions(prev => {
      const newSuggestions = { ...prev };
      delete newSuggestions[fieldName];
      return newSuggestions;
    });
  };

  // Função para renderizar feedback do campo
  const renderFieldFeedback = (fieldName: string) => {
    const isFieldAnalyzing = analyzingFields.has(fieldName);
    const suggestion = fieldSuggestions[fieldName];

    if (isFieldAnalyzing) {
      return (
        <div className="mt-2 p-3 bg-blue-50 border border-blue-200 rounded-lg">
          <div className="flex items-center gap-2 text-blue-700">
            <Loader2 className="w-4 h-4 animate-spin" />
            <span className="text-sm font-medium">Analisando com IA...</span>
          </div>
        </div>
      );
    }

    if (suggestion?.suggestion) {
      return (
        <div className="mt-2 p-3 bg-yellow-50 border border-yellow-200 rounded-lg">
          <div className="flex items-start justify-between gap-2">
            <div className="flex-1">
              <div className="flex items-center gap-2 mb-1">
                <Sparkles className="w-4 h-4 text-yellow-600" />
                <span className="text-sm font-medium text-yellow-800">Sugestão da IA</span>
              </div>
              <p className="text-sm text-yellow-700">{suggestion.suggestion}</p>
            </div>
          </div>
          <div className="flex gap-2 mt-3">
            <button
              type="button"
              onClick={() => applyFieldSuggestion(fieldName, suggestion.suggestion)}
              className="px-3 py-1 bg-yellow-600 text-white text-xs rounded hover:bg-yellow-700"
            >
              Aplicar
            </button>
            <button
              type="button"
              onClick={() => dismissFieldSuggestion(fieldName)}
              className="px-3 py-1 bg-gray-200 text-gray-700 text-xs rounded hover:bg-gray-300"
            >
              Dispensar
            </button>
          </div>
        </div>
      );
    }

    if (suggestion?.praise) {
      return (
        <div className="mt-2 p-3 bg-green-50 border border-green-200 rounded-lg">
          <div className="flex items-center gap-2 text-green-700">
            <span className="text-green-500">✓</span>
            <span className="text-sm font-medium">{suggestion.praise}</span>
          </div>
        </div>
      );
    }

    return null;
  };

  // Limpar timers ao desmontar o componente
  useEffect(() => {
    return () => {
      Object.values(fieldTimers).forEach(timer => clearTimeout(timer as NodeJS.Timeout));
    };
  }, [fieldTimers]);

  const isFormDataChanged = () => {
    return JSON.stringify(formData) !== lastAnalyzedData;
  };

  // Seções do formulário
  const sections = [
    {
      id: 'basic',
      title: 'Informações Básicas',
      required: true,
      fields: ['title', 'category']
    },
    {
      id: '5w2h',
      title: 'Metodologia 5W2H',
      required: true,
      fields: ['what', 'who', 'when', 'where', 'why', 'how', 'howMuch']
    },
    {
      id: 'additional',
      title: 'Informações Adicionais',
      required: false,
      fields: ['difficulties', 'resources', 'attentionPoints']
    },
    {
      id: 'summary',
      title: 'Resumo',
      required: true,
      fields: ['description', 'benefits']
    }
  ];

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));

    // Limpar timer anterior se existir
    if (fieldTimers[name]) {
      clearTimeout(fieldTimers[name]);
    }

    // Limpar sugestões anteriores do campo
    setFieldSuggestions(prev => {
      const newSuggestions = { ...prev };
      delete newSuggestions[name];
      return newSuggestions;
    });

    // Resetar status do campo
    setFieldStatus(prev => {
      const newStatus = { ...prev };
      delete newStatus[name];
      return newStatus;
    });

    // Configurar novo timer para análise (10 segundos)
    if (value.trim().length > 5) {
      const timer = setTimeout(() => {
        analyzeField(name, value);
      }, 10000); // 10 segundos

      setFieldTimers(prev => ({ ...prev, [name]: timer }));
    }

    // Detectar ideias similares
    if (name === 'title' && value.length > 5) {
      const similar = ideas
        .filter(idea => 
          idea.title.toLowerCase().includes(value.toLowerCase()) ||
          idea.description.toLowerCase().includes(value.toLowerCase())
        )
        .map(idea => idea.title);
      setSimilarIdeas(similar.slice(0, 3));
    }
  };

  const toggleSection = (sectionId: string) => {
    const newExpanded = new Set(expandedSections);
    if (newExpanded.has(sectionId)) {
      newExpanded.delete(sectionId);
    } else {
      newExpanded.add(sectionId);
    }
    setExpandedSections(newExpanded);
  };

  const handleAIAnalysis = async () => {
    if (!configuration?.isActive) {
      alert('A IA não está configurada ou ativa. Configure nas configurações administrativas.');
      return;
    }

    setIsAnalyzing(true);
    try {
      const response = await analyzeIdea(formData);
      if (response.success) {
        setAiSuggestions(response);
        setShowAIPanel(true);
        setLastAnalyzedData(JSON.stringify(formData));
      } else {
        alert(response.error || 'Erro ao analisar ideia com IA');
      }
    } catch (error) {
      console.error('Erro na análise:', error);
      alert('Erro ao conectar com a IA');
    } finally {
      setIsAnalyzing(false);
    }
  };

  const applySuggestion = (field: keyof IdeaFormData, value: string) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const applyAllSuggestions = () => {
    if (aiSuggestions?.suggestions) {
      setFormData(prev => ({
        ...prev,
        ...aiSuggestions.suggestions
      }));
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      // Converter para o formato esperado pelo IdeasContext
      const ideaData = {
        title: formData.title || '',
        description: formData.description || '',
        category: formData.category || '',
        benefits: formData.benefits || '',
        // Dados adicionais podem ser armazenados em um campo extra
        metadata: {
          what: formData.what,
          who: formData.who,
          when: formData.when,
          where: formData.where,
          why: formData.why,
          how: formData.how,
          howMuch: formData.howMuch,
          difficulties: formData.difficulties,
          resources: formData.resources,
          attentionPoints: formData.attentionPoints
        }
      };
      
      await submitIdea(ideaData);
      navigate('/my-ideas');
    } catch (error) {
      console.error('Erro ao submeter ideia:', error);
    }
  };

  return (
    <div className="max-w-6xl mx-auto">
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Formulário Principal */}
        <div className="lg:col-span-2">
          <div className="bg-white rounded-lg shadow-md p-6">
            <h2 className="text-2xl font-bold text-gray-900 mb-6 flex items-center gap-2">
              <Lightbulb className="w-6 h-6 text-primary-600" />
              Nova Ideia de Inovação
            </h2>

            <form onSubmit={handleSubmit} className="space-y-6">
              {sections.map((section) => (
                <div key={section.id} className="border border-gray-200 rounded-lg">
                  <button
                    type="button"
                    onClick={() => toggleSection(section.id)}
                    className="w-full px-4 py-3 flex items-center justify-between bg-gray-50 hover:bg-gray-100 rounded-t-lg"
                  >
                    <div className="flex items-center gap-2">
                      <span className="font-medium">{section.title}</span>
                      {section.required && <span className="text-red-500">*</span>}
                    </div>
                    {expandedSections.has(section.id) ? (
                      <ChevronUp className="w-5 h-5" />
                    ) : (
                      <ChevronDown className="w-5 h-5" />
                    )}
                  </button>

                  {expandedSections.has(section.id) && (
                    <div className="p-4 space-y-4">
                      {section.id === 'basic' && (
                        <>
                          <div>                            <label htmlFor="title" className="label">
                              Qual é sua ideia? *
                            </label>
                            <input
                              type="text"
                              id="title"
                              name="title"
                              value={formData.title || ''}
                              onChange={handleChange}
                              required
                              className="input"
                              placeholder="Ex: Trocar marca do papel higiênico para economizar"
                            />
                            {renderFieldFeedback('title')}
                          </div>

                          <div>                            <label htmlFor="category" className="label">
                              Em que área se encaixa? *
                            </label>
                            <select
                              id="category"
                              name="category"
                              value={formData.category || ''}
                              onChange={handleChange}
                              required
                              className="input"
                            >
                              <option value="">Escolha a área mais próxima</option>
                              {categories.map(cat => (
                                <option key={cat} value={cat}>{cat}</option>
                              ))}
                            </select>
                          </div>
                        </>
                      )}

                      {section.id === '5w2h' && (
                        <>
                          <div>                            <label htmlFor="what" className="label">
                              Qual seria sua ideia? *
                            </label>
                            <textarea
                              id="what"
                              name="what"
                              value={formData.what || ''}
                              onChange={handleChange}
                              rows={3}
                              className="input"
                              placeholder="Conte sua ideia de forma simples. Ex: Mudar a marca do papel higiênico porque o atual gasta muito..."
                            />
                            {renderFieldFeedback('what')}
                          </div>

                          <div>                            <label htmlFor="who" className="label">
                              Quem seria responsável? *
                            </label>
                            <textarea
                              id="who"
                              name="who"
                              value={formData.who || ''}
                              onChange={handleChange}
                              rows={2}
                              className="input"
                              placeholder="Quem cuidaria disso? Ex: Pessoal da limpeza, RH, compras..."
                            />
                            {renderFieldFeedback('who')}
                          </div>

                          <div>                            <label htmlFor="when" className="label">
                              Quando fazer isso? *
                            </label>
                            <textarea
                              id="when"
                              name="when"
                              value={formData.when || ''}
                              onChange={handleChange}
                              rows={2}
                              className="input"
                              placeholder="Quando seria bom implementar? Ex: No próximo mês, no fim do ano..."
                            />
                            {renderFieldFeedback('when')}
                          </div>

                          <div>                            <label htmlFor="where" className="label">
                              Onde seria aplicado? *
                            </label>
                            <textarea
                              id="where"
                              name="where"
                              value={formData.where || ''}
                              onChange={handleChange}
                              rows={2}
                              className="input"
                              placeholder="Em que local da empresa? Ex: Em todos os banheiros, só no escritório..."
                            />
                            {renderFieldFeedback('where')}
                          </div>

                          <div>                            <label htmlFor="why" className="label">
                              Por que é importante? *
                            </label>
                            <textarea
                              id="why"
                              name="why"
                              value={formData.why || ''}
                              onChange={handleChange}
                              rows={3}
                              className="input"
                              placeholder="Por que sua ideia é boa? Ex: Porque vai economizar dinheiro, melhorar o ambiente..."
                            />
                            {renderFieldFeedback('why')}
                          </div>

                          <div>                            <label htmlFor="how" className="label">
                              Como fazer isso? *
                            </label>
                            <textarea
                              id="how"
                              name="how"
                              value={formData.how || ''}
                              onChange={handleChange}
                              rows={3}
                              className="input"
                              placeholder="Como colocar sua ideia em prática? Ex: Primeiro testar em um banheiro, depois em todos..."
                            />
                            {renderFieldFeedback('how')}
                          </div>

                          <div>                            <label htmlFor="howMuch" className="label">
                              Quanto vai custar? *
                            </label>
                            <textarea
                              id="howMuch"
                              name="howMuch"
                              value={formData.howMuch || ''}
                              onChange={handleChange}
                              rows={2}
                              className="input"
                              placeholder="Você tem ideia do custo? Ex: Mais barato que o atual, investimento de R$ 500..."
                            />
                            {renderFieldFeedback('howMuch')}
                          </div>
                        </>
                      )}

                      {section.id === 'additional' && (
                        <>
                          <div>
                            <label htmlFor="difficulties" className="label">
                              Dificuldades
                            </label>
                            <textarea
                              id="difficulties"
                              name="difficulties"
                              value={formData.difficulties || ''}
                              onChange={handleChange}
                              rows={3}
                              className="input"
                              placeholder="Possíveis obstáculos e desafios"
                            />
                            {renderFieldFeedback('difficulties')}
                          </div>

                          <div>
                            <label htmlFor="resources" className="label">
                              Recursos Necessários
                            </label>
                            <textarea
                              id="resources"
                              name="resources"
                              value={formData.resources || ''}
                              onChange={handleChange}
                              rows={3}
                              className="input"
                              placeholder="Recursos humanos, financeiros e materiais"
                            />
                            {renderFieldFeedback('resources')}
                          </div>

                          <div>
                            <label htmlFor="attentionPoints" className="label">
                              Pontos de Atenção
                            </label>
                            <textarea
                              id="attentionPoints"
                              name="attentionPoints"
                              value={formData.attentionPoints || ''}
                              onChange={handleChange}
                              rows={3}
                              className="input"
                              placeholder="Riscos e considerações importantes"
                            />
                            {renderFieldFeedback('attentionPoints')}
                          </div>
                        </>
                      )}

                      {section.id === 'summary' && (
                        <>
                          <div>                            <label htmlFor="description" className="label">
                              Conte-nos mais sobre sua ideia *
                            </label>
                            <textarea
                              id="description"
                              name="description"
                              value={formData.description || ''}
                              onChange={handleChange}
                              required
                              rows={4}
                              className="input"
                              placeholder="Explique melhor sua ideia. A IA vai te ajudar a melhorar o texto..."
                            />
                            {renderFieldFeedback('description')}
                          </div>

                          <div>                            <label htmlFor="benefits" className="label">
                              Que benefícios você imagina? *
                            </label>
                            <textarea
                              id="benefits"
                              name="benefits"
                              value={formData.benefits || ''}
                              onChange={handleChange}
                              required
                              rows={3}
                              className="input"
                              placeholder="Como sua ideia pode ajudar a empresa? Ex: economizar dinheiro, melhorar o ambiente..."
                            />
                            {renderFieldFeedback('benefits')}
                          </div>
                        </>
                      )}
                    </div>
                  )}
                </div>
              ))}

              {/* Ideias Similares */}
              {similarIdeas.length > 0 && (
                <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4">
                  <div className="flex items-start space-x-2">
                    <AlertCircle className="w-5 h-5 text-yellow-600 mt-0.5" />
                    <div>
                      <p className="text-sm font-medium text-yellow-800">Ideias similares encontradas:</p>
                      <ul className="mt-2 space-y-1">
                        {similarIdeas.map((idea, index) => (
                          <li key={index} className="text-sm text-yellow-700">• {idea}</li>
                        ))}
                      </ul>
                    </div>
                  </div>
                </div>
              )}

              {/* Botões de Ação */}
              <div className="flex flex-col sm:flex-row justify-between gap-4 pt-4 border-t">
                <div className="flex gap-2">
                  <button
                    type="button"
                    onClick={handleAIAnalysis}
                    disabled={isAnalyzing || !configuration?.isActive}
                    className="flex items-center gap-2 px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 disabled:opacity-50"
                  >
                    {isAnalyzing ? (
                      <Loader2 className="w-4 h-4 animate-spin" />
                    ) : (
                      <Sparkles className="w-4 h-4" />
                    )}
                    {isAnalyzing ? 'Analisando...' : 'Analisar com IA'}
                  </button>

                  {isFormDataChanged() && aiSuggestions && (
                    <button
                      type="button"
                      onClick={handleAIAnalysis}
                      className="flex items-center gap-2 px-3 py-2 text-blue-600 border border-blue-300 rounded-md hover:bg-blue-50"
                    >
                      <RefreshCw className="w-4 h-4" />
                      Reanalisar
                    </button>
                  )}
                </div>

                <div className="flex gap-2">
                  <button
                    type="button"
                    className="flex items-center gap-2 px-4 py-2 text-gray-600 border border-gray-300 rounded-md hover:bg-gray-50"
                  >
                    <Save className="w-4 h-4" />
                    Salvar Rascunho
                  </button>

                  <button
                    type="submit"
                    className="flex items-center gap-2 px-4 py-2 bg-primary-600 text-white rounded-md hover:bg-primary-700"
                  >
                    <Send className="w-4 h-4" />
                    Enviar Ideia
                  </button>
                </div>
              </div>
            </form>
          </div>
        </div>

        {/* Painel de IA */}
        <div className="lg:col-span-1">
          {configuration?.isActive ? (
            <div className={`bg-white rounded-lg shadow-md transition-all duration-300 ${showAIPanel ? 'block' : 'hidden lg:block'}`}>
              <div className="p-4 border-b bg-gradient-to-r from-blue-50 to-purple-50">
                <h3 className="font-semibold text-gray-900 flex items-center gap-2">
                  <Sparkles className="w-5 h-5 text-blue-600" />
                  Assistente de IA
                </h3>
                <p className="text-sm text-gray-600 mt-1">
                  Analise sua ideia e receba sugestões de melhorias
                </p>
              </div>

              {!aiSuggestions ? (
                <div className="p-4">
                  <p className="text-sm text-gray-500 text-center py-8">
                    Preencha alguns campos e clique em "Analisar com IA" para receber sugestões personalizadas.
                  </p>
                </div>
              ) : (
                <div className="p-4 space-y-4 max-h-96 overflow-y-auto">
                  {/* Botão para aplicar todas as sugestões */}
                  <button
                    onClick={applyAllSuggestions}
                    className="w-full px-3 py-2 bg-green-600 text-white text-sm rounded-md hover:bg-green-700"
                  >
                    Aplicar Todas as Sugestões
                  </button>

                  {/* Sugestões por campo */}
                  {Object.entries(aiSuggestions.suggestions || {}).map(([field, value]) => {
                    if (!value || field === 'category') return null;
                    
                    const fieldLabels: { [key: string]: string } = {
                      title: 'Título',
                      what: 'O quê?',
                      who: 'Quem?',
                      when: 'Quando?',
                      where: 'Onde?',
                      why: 'Por quê?',
                      how: 'Como?',
                      howMuch: 'Quanto custará?',
                      difficulties: 'Dificuldades',
                      resources: 'Recursos',
                      attentionPoints: 'Pontos de Atenção',
                      description: 'Descrição',
                      benefits: 'Benefícios'
                    };

                    return (
                      <div key={field} className="border border-gray-200 rounded-lg p-3">
                        <div className="flex items-center justify-between mb-2">
                          <span className="text-sm font-medium text-gray-700">
                            {fieldLabels[field] || field}
                          </span>
                          <button
                            onClick={() => applySuggestion(field as keyof IdeaFormData, value as string)}
                            className="text-xs px-2 py-1 bg-blue-100 text-blue-700 rounded hover:bg-blue-200"
                          >
                            Aplicar
                          </button>
                        </div>
                        <p className="text-sm text-gray-600 line-clamp-3">{value as string}</p>
                      </div>
                    );
                  })}

                  {/* Melhorias sugeridas */}
                  {aiSuggestions.improvements && aiSuggestions.improvements.length > 0 && (
                    <div className="border-t pt-4">
                      <h4 className="font-medium text-gray-900 mb-2">Melhorias Sugeridas:</h4>
                      <ul className="space-y-1">
                        {aiSuggestions.improvements.map((improvement: string, index: number) => (
                          <li key={index} className="text-sm text-gray-600 flex items-start gap-2">
                            <span className="text-blue-500 mt-1">•</span>
                            <span>{improvement}</span>
                          </li>
                        ))}
                      </ul>
                    </div>
                  )}

                  {/* Ideias relacionadas */}
                  {aiSuggestions.relatedIdeas && aiSuggestions.relatedIdeas.length > 0 && (
                    <div className="border-t pt-4">
                      <h4 className="font-medium text-gray-900 mb-2">Ideias Relacionadas:</h4>
                      <ul className="space-y-1">
                        {aiSuggestions.relatedIdeas.map((idea: string, index: number) => (
                          <li key={index} className="text-sm text-gray-600 flex items-start gap-2">
                            <span className="text-purple-500 mt-1">•</span>
                            <span>{idea}</span>
                          </li>
                        ))}
                      </ul>
                    </div>
                  )}
                </div>
              )}
            </div>
          ) : (
            <div className="bg-white rounded-lg shadow-md p-6 text-center">
              <AlertCircle className="w-12 h-12 text-gray-400 mx-auto mb-3" />
              <h3 className="font-medium text-gray-900 mb-2">IA Indisponível</h3>
              <p className="text-sm text-gray-500">
                A assistência de IA não está configurada. Entre em contato com o administrador.
              </p>
            </div>
          )}        </div>
      </div>
    </div>
  );
};

export default EnhancedIdeaForm;
