import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { useIdeas } from '../../contexts/IdeasContext';
import { useAI } from '../../contexts/AIContext';
import { categories } from '../../data/mockData';
import { IdeaFormData } from '../../types';
import { 
  Lightbulb, 
  AlertCircle, 
  Sparkles, 
  Loader2, 
  ChevronDown, 
  ChevronUp,
  Send
} from 'lucide-react';

export const EnhancedIdeaForm: React.FC = () => {
  const navigate = useNavigate();
  const { submitIdea, ideas } = useIdeas();
  const { configuration } = useAI();
  
  const [formData, setFormData] = useState<Partial<IdeaFormData>>({
    title: '',
    category: '',
    what: '',
    who: '',
    when: '',
    where: '',
    why: '',
    how: '',
    howMuch: '',
    difficulties: '',
    resources: '',
    attentionPoints: '',
    description: '',
    benefits: ''
  });

  const [similarIdeas, setSimilarIdeas] = useState<string[]>([]);
  const [expandedSections, setExpandedSections] = useState<Set<string>>(new Set(['basic']));

  // Estados para análise campo a campo
  const [fieldSuggestions, setFieldSuggestions] = useState<Record<string, any>>({});
  const [fieldTimers, setFieldTimers] = useState<Record<string, NodeJS.Timeout>>({});
  const [analyzingFields, setAnalyzingFields] = useState<Set<string>>(new Set());

  // Função para gerar sugestões inteligentes baseadas no conteúdo
  const generateSmartSuggestion = (fieldName: string, currentValue: string): string => {
    const value = currentValue.toLowerCase();
    
    // Sugestões específicas e inteligentes baseadas no conteúdo
    switch (fieldName) {
      case 'title':
        if (value.includes('papel') || value.includes('higiene')) {
          return `${currentValue} - Proposta de otimização para redução de custos e melhoria da qualidade`;
        }
        if (value.includes('sistema') || value.includes('software')) {
          return `${currentValue}: Sistema inovador para modernização dos processos da Proeng`;
        }
        return `${currentValue} - Inovação estratégica para crescimento da Proeng`;
        
      case 'description':
        if (value.includes('papel') || value.includes('higiene')) {
          return `${currentValue}

Esta proposta visa otimizar os custos operacionais da empresa através da substituição por materiais de melhor qualidade-preço. Baseado em observação prática do consumo diário, a mudança pode resultar em economia significativa sem comprometer o bem-estar dos colaboradores.

Principais benefícios esperados:
• Redução do consumo devido à maior qualidade do produto
• Economia mensal nos custos operacionais
• Melhoria da sustentabilidade ambiental
• Maior satisfação dos usuários`;
        }
        if (value.includes('processo') || value.includes('melhoria')) {
          return `${currentValue}

Esta inovação propõe modernizar nossos processos atuais, aumentando a eficiência operacional e reduzindo retrabalhos. A implementação gradual permitirá adaptação da equipe e mensuração dos resultados de forma controlada.

Impactos esperados:
• Otimização de tempo e recursos
• Melhoria da qualidade dos entregáveis
• Maior satisfação da equipe
• Fortalecimento da competitividade da Proeng`;
        }
        return `${currentValue}

Esta ideia representa uma oportunidade de inovação alinhada com os objetivos estratégicos da Proeng, contribuindo para o crescimento sustentável da empresa e melhoria contínua de nossos processos.`;
        
      case 'benefits':
        if (value.includes('economia') || value.includes('custo')) {
          return `${currentValue}

• Redução de custos operacionais mensais
• Melhoria da sustentabilidade financeira
• Otimização do orçamento para outros investimentos estratégicos
• Maior competitividade no mercado
• ROI positivo em médio prazo`;
        }
        return `${currentValue}

• Aumento da eficiência operacional
• Melhoria do ambiente de trabalho
• Fortalecimento da imagem da Proeng como empresa inovadora
• Maior satisfação dos colaboradores
• Crescimento sustentável da organização`;
        
      case 'what':
        if (value.includes('papel') || value.includes('higiene')) {
          return `Substituição da marca atual de papel higiênico por uma opção de melhor qualidade que reduz o consumo devido à maior resistência e absorção. A proposta inclui teste piloto, análise de custo-benefício e implementação gradual em todas as unidades da Proeng.`;
        }
        return `${currentValue} - Implementação de solução inovadora que moderniza processos atuais, otimiza recursos e melhora resultados operacionais da Proeng.`;
        
      case 'who':
        return `Equipe de implementação: funcionários da área afetada, gestores responsáveis, equipe de compras/suprimentos da Proeng, e colaboradores que utilizam diariamente o recurso para validação prática.`;
        
      case 'when':
        return `Cronograma sugerido: 
Fase 1 (30 dias) - Pesquisa de mercado e cotações
Fase 2 (15 dias) - Teste piloto em área específica
Fase 3 (15 dias) - Avaliação de resultados e feedback
Fase 4 (30 dias) - Implementação completa caso aprovado`;
        
      case 'where':
        return `Implementação inicial: Escritório principal da Proeng para teste piloto. Após validação positiva, expansão gradual para todas as unidades e setores da empresa, priorizando áreas de maior impacto.`;
        
      case 'why':
        if (value.includes('economia') || value.includes('custo')) {
          return `${currentValue}

Justificativa estratégica: Observação prática demonstra consumo excessivo com a solução atual devido à baixa qualidade, gerando desperdício e aumentando custos desnecessariamente. A mudança estratégica alinha economia com sustentabilidade e bem-estar dos colaboradores, contribuindo para os objetivos de otimização da Proeng.`;
        }
        return `${currentValue}

Esta necessidade surge da busca contínua por melhorias operacionais que fortaleçam a posição competitiva da Proeng no mercado, otimizando recursos e promovendo inovação constante em todos os processos organizacionais.`;
        
      case 'how':
        return `Metodologia de implementação: 
1) Pesquisa de mercado e análise de fornecedores
2) Análise técnica comparativa e financeira
3) Teste controlado com amostra representativa
4) Coleta estruturada de feedback dos usuários
5) Análise quantitativa e qualitativa dos resultados
6) Decisão baseada em dados e evidências
7) Implementação total com acompanhamento contínuo`;
        
      case 'howMuch':
        return `Investimento estimado: 
• Custo inicial de pesquisa e teste piloto: baixo impacto orçamentário
• Investimento principal: aquisição do novo produto/solução
• Expectativa de retorno: redução de consumo e economia mensal
• ROI estimado: 3-6 meses
• Benefício contínuo: economia permanente nos custos operacionais`;
        
      default:
        return `${currentValue} - Detalhamento adicional alinhado com os objetivos estratégicos da Proeng.`;
    }
  };

  // Função para analisar um campo específico
  const analyzeField = useCallback(async (fieldName: string, value: string) => {
    if (!configuration?.isActive || !value.trim() || value.length < 5) {
      return;
    }

    setAnalyzingFields(prev => new Set(Array.from(prev).concat(fieldName)));

    try {
      // Simular análise de IA com sugestões inteligentes
      const mockAnalysis = await new Promise<{suggestion?: string, praise?: string}>((resolve) => {
        setTimeout(() => {
          // Determinar se precisa de sugestão baseado no tamanho e qualidade do conteúdo
          const needsSuggestion = value.length < 30 || (!value.includes('.') && fieldName !== 'title');
          
          if (needsSuggestion) {
            const smartSuggestion = generateSmartSuggestion(fieldName, value);
            resolve({ suggestion: smartSuggestion });
          } else {
            const praises = [
              'Excelente! Sua descrição está muito clara e completa.',
              'Ótimo trabalho! Informação bem estruturada e relevante.',
              'Perfeito! Este conteúdo está alinhado com nossos objetivos.',
              'Muito bom! Descrição objetiva e bem fundamentada.',
              'Excelente contribuição! Sua ideia está bem desenvolvida.'
            ];
            
            resolve({
              praise: praises[Math.floor(Math.random() * praises.length)]
            });
          }
        }, 2000);
      });

      setFieldSuggestions(prev => ({
        ...prev,
        [fieldName]: mockAnalysis
      }));

    } catch (error) {
      console.error('Erro na análise do campo:', error);
    } finally {
      setAnalyzingFields(prev => {
        const newSet = new Set(prev);
        newSet.delete(fieldName);
        return newSet;
      });
    }
  }, [configuration]);

  // Função para aplicar sugestão de campo
  const applyFieldSuggestion = (fieldName: string, suggestion: string) => {
    setFormData(prev => ({ ...prev, [fieldName]: suggestion }));
    // Limpar a sugestão após aplicar
    setFieldSuggestions(prev => {
      const newSuggestions = { ...prev };
      delete newSuggestions[fieldName];
      return newSuggestions;
    });
  };

  // Função para dispensar sugestão
  const dismissFieldSuggestion = (fieldName: string) => {
    setFieldSuggestions(prev => {
      const newSuggestions = { ...prev };
      delete newSuggestions[fieldName];
      return newSuggestions;
    });
  };

  // Função para renderizar feedback do campo
  const renderFieldFeedback = (fieldName: string) => {
    const isFieldAnalyzing = analyzingFields.has(fieldName);
    const suggestion = fieldSuggestions[fieldName];

    if (isFieldAnalyzing) {
      return (
        <div className="mt-2 p-3 bg-blue-50 border border-blue-200 rounded-lg">
          <div className="flex items-center gap-2 text-blue-700">
            <Loader2 className="w-4 h-4 animate-spin" />
            <span className="text-sm font-medium">✨ A IA está analisando sua ideia...</span>
          </div>
        </div>
      );
    }

    if (suggestion?.suggestion) {
      return (
        <div className="mt-2 p-4 bg-gradient-to-r from-yellow-50 to-orange-50 border border-yellow-200 rounded-lg">
          <div className="flex items-start justify-between gap-2 mb-3">
            <div className="flex-1">
              <div className="flex items-center gap-2 mb-2">
                <Sparkles className="w-4 h-4 text-yellow-600" />
                <span className="text-sm font-medium text-yellow-800">💡 A IA melhorou sua ideia!</span>
              </div>
              <div className="bg-white p-3 rounded border border-yellow-200 max-h-40 overflow-y-auto">
                <p className="text-sm text-gray-700 whitespace-pre-line">{suggestion.suggestion}</p>
              </div>
            </div>
          </div>
          <div className="flex gap-2">
            <button
              type="button"
              onClick={() => applyFieldSuggestion(fieldName, suggestion.suggestion)}
              className="px-4 py-2 bg-green-600 text-white text-sm rounded-lg hover:bg-green-700 transition-colors font-medium"
            >
              ✅ Usar esta versão
            </button>
            <button
              type="button"
              onClick={() => dismissFieldSuggestion(fieldName)}
              className="px-4 py-2 bg-gray-300 text-gray-700 text-sm rounded-lg hover:bg-gray-400 transition-colors"
            >
              ❌ Dispensar
            </button>
          </div>
        </div>
      );
    }

    if (suggestion?.praise) {
      return (
        <div className="mt-2 p-3 bg-green-50 border border-green-200 rounded-lg">
          <div className="flex items-center gap-2 text-green-700">
            <span className="text-green-500">✅</span>
            <span className="text-sm font-medium">{suggestion.praise}</span>
          </div>
        </div>
      );
    }

    return null;
  };

  // Debounce para análise de campo
  const handleFieldChange = useCallback((fieldName: string, value: string) => {
    setFormData(prev => ({ ...prev, [fieldName]: value }));

    // Limpar timer anterior se existir
    if (fieldTimers[fieldName]) {
      clearTimeout(fieldTimers[fieldName]);
    }

    // Limpar sugestões anteriores quando o usuário começar a digitar
    if (fieldSuggestions[fieldName]) {
      setFieldSuggestions(prev => {
        const newSuggestions = { ...prev };
        delete newSuggestions[fieldName];
        return newSuggestions;
      });
    }

    // Definir novo timer para análise após 10 segundos (só se não está vazio)
    if (value.trim().length > 0) {
      const newTimer = setTimeout(() => {
        analyzeField(fieldName, value);
      }, 10000); // 10 segundos

      setFieldTimers(prev => ({
        ...prev,
        [fieldName]: newTimer
      }));
    }
  }, [fieldTimers, fieldSuggestions, analyzeField]);

  // Cleanup dos timers
  useEffect(() => {
    return () => {
      Object.values(fieldTimers).forEach(timer => {
        if (timer) clearTimeout(timer);
      });
    };
  }, [fieldTimers]);

  // Verificar ideias similares
  useEffect(() => {
    if (formData.title && formData.title.length > 3) {
      const similar = ideas.filter(idea => 
        idea.title.toLowerCase().includes(formData.title!.toLowerCase()) ||
        formData.title!.toLowerCase().includes(idea.title.toLowerCase())
      ).map(idea => idea.title);
      setSimilarIdeas(similar);
    } else {
      setSimilarIdeas([]);
    }
  }, [formData.title, ideas]);

  const toggleSection = (section: string) => {
    setExpandedSections(prev => {
      const newSet = new Set(prev);
      if (newSet.has(section)) {
        newSet.delete(section);
      } else {
        newSet.add(section);
      }
      return newSet;
    });
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    
    if (!formData.title || !formData.category || !formData.description) {
      alert('Preencha os campos obrigatórios: Título, Categoria e Descrição');
      return;
    }

    try {
      await submitIdea(formData as IdeaFormData);
      navigate('/my-ideas');
    } catch (error) {
      console.error('Erro ao submeter ideia:', error);
      alert('Erro ao submeter ideia. Tente novamente.');
    }
  };

  const renderFormField = (
    label: string,
    fieldName: keyof IdeaFormData,
    placeholder: string,
    required: boolean = false,
    type: 'input' | 'textarea' | 'select' = 'input',
    options?: Array<{value: string, label: string}>
  ) => {
    const value = formData[fieldName] || '';
    
    return (
      <div className="space-y-2">
        <label className="block text-sm font-medium text-gray-700">
          {label} {required && <span className="text-red-500">*</span>}
        </label>
        
        {type === 'select' ? (
          <select
            value={value}
            onChange={(e) => handleFieldChange(fieldName, e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            required={required}
          >
            <option value="">{placeholder}</option>
            {options?.map(option => (
              <option key={option.value} value={option.value}>
                {option.label}
              </option>
            ))}
          </select>
        ) : type === 'textarea' ? (
          <textarea
            value={value}
            onChange={(e) => handleFieldChange(fieldName, e.target.value)}
            placeholder={placeholder}
            rows={4}
            className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent resize-vertical"
            required={required}
          />
        ) : (
          <input
            type="text"
            value={value}
            onChange={(e) => handleFieldChange(fieldName, e.target.value)}
            placeholder={placeholder}
            className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            required={required}
          />
        )}
        
        {renderFieldFeedback(fieldName)}
      </div>
    );
  };

  return (
    <div className="max-w-4xl mx-auto p-6">
      <div className="bg-white rounded-lg shadow-md p-6">
        <div className="text-center mb-8">
          <h1 className="text-3xl font-bold text-gray-900 flex items-center justify-center gap-3 mb-3">
            <Lightbulb className="w-8 h-8 text-yellow-500" />
            ✨ Compartilhe sua ideia
          </h1>
          <p className="text-lg text-gray-600 mb-2">
            A IA vai te ajudar a desenvolver sua ideia!
          </p>
          <p className="text-sm text-gray-500">
            Não se preocupe em escrever perfeito - nossa IA vai melhorar seu texto conforme você digita.
          </p>
        </div>

        <form onSubmit={handleSubmit} className="space-y-8">
          {/* Seção Básica */}
          <div className="border rounded-lg p-6 bg-gradient-to-r from-blue-50 to-indigo-50">
            <button
              type="button"
              onClick={() => toggleSection('basic')}
              className="flex items-center justify-between w-full text-left"
            >
              <h2 className="text-xl font-semibold text-gray-800">1️⃣ Informações Básicas</h2>
              {expandedSections.has('basic') ? (
                <ChevronUp className="w-5 h-5 text-gray-500" />
              ) : (
                <ChevronDown className="w-5 h-5 text-gray-500" />
              )}
            </button>
            
            {expandedSections.has('basic') && (
              <div className="mt-6 space-y-6">
                {renderFormField('Qual é sua ideia?', 'title', 'Ex: Trocar marca do papel higiênico para economizar', true)}
                  {renderFormField(
                  'Em que área se encaixa?', 
                  'category', 
                  'Escolha a área mais próxima', 
                  true, 
                  'select',
                  categories.map(cat => ({ value: cat, label: cat }))
                )}
              </div>
            )}
          </div>

          {/* Seção Descrição */}
          <div className="border rounded-lg p-6 bg-gradient-to-r from-green-50 to-emerald-50">
            <button
              type="button"
              onClick={() => toggleSection('description')}
              className="flex items-center justify-between w-full text-left"
            >
              <h2 className="text-xl font-semibold text-gray-800">2️⃣ Conte sua ideia</h2>
              {expandedSections.has('description') ? (
                <ChevronUp className="w-5 h-5 text-gray-500" />
              ) : (
                <ChevronDown className="w-5 h-5 text-gray-500" />
              )}
            </button>
            
            {expandedSections.has('description') && (
              <div className="mt-6 space-y-6">
                {renderFormField('Conte-nos mais sobre sua ideia', 'description', 'Explique melhor sua ideia. A IA vai te ajudar a melhorar o texto...', true, 'textarea')}
                
                {renderFormField('Que benefícios você imagina?', 'benefits', 'Como sua ideia pode ajudar a empresa? Ex: economizar dinheiro, melhorar o ambiente...', true, 'textarea')}
              </div>
            )}
          </div>

          {/* Seção 5W2H */}
          <div className="border rounded-lg p-6 bg-gradient-to-r from-purple-50 to-violet-50">
            <button
              type="button"
              onClick={() => toggleSection('5w2h')}
              className="flex items-center justify-between w-full text-left"
            >
              <h2 className="text-xl font-semibold text-gray-800">3️⃣ Vamos detalhar? (Opcional)</h2>
              {expandedSections.has('5w2h') ? (
                <ChevronUp className="w-5 h-5 text-gray-500" />
              ) : (
                <ChevronDown className="w-5 h-5 text-gray-500" />
              )}
            </button>
            
            {expandedSections.has('5w2h') && (
              <div className="mt-6 space-y-6">
                <p className="text-sm text-gray-600 italic">
                  💡 Estes campos são opcionais, mas ajudam a estruturar melhor sua ideia. A IA vai te ajudar a preenchê-los!
                </p>
                
                {renderFormField('Qual seria sua ideia?', 'what', 'Conte sua ideia de forma simples. Ex: Mudar a marca do papel higiênico porque o atual gasta muito...', false, 'textarea')}
                {renderFormField('Quem seria responsável?', 'who', 'Quem cuidaria disso? Ex: Pessoal da limpeza, RH, compras...', false, 'textarea')}
                {renderFormField('Quando fazer isso?', 'when', 'Quando seria bom implementar? Ex: No próximo mês, no fim do ano...', false, 'textarea')}
                {renderFormField('Onde seria aplicado?', 'where', 'Em que local da empresa? Ex: Em todos os banheiros, só no escritório...', false, 'textarea')}
                {renderFormField('Por que é importante?', 'why', 'Por que sua ideia é boa? Ex: Porque vai economizar dinheiro, melhorar o ambiente...', false, 'textarea')}
                {renderFormField('Como fazer isso?', 'how', 'Como colocar sua ideia em prática? Ex: Primeiro testar em um banheiro, depois em todos...', false, 'textarea')}
                {renderFormField('Quanto vai custar?', 'howMuch', 'Você tem ideia do custo? Ex: Mais barato que o atual, investimento de R$ 500...', false, 'textarea')}
              </div>
            )}
          </div>

          {/* Seção Complementar */}
          <div className="border rounded-lg p-6 bg-gradient-to-r from-orange-50 to-amber-50">
            <button
              type="button"
              onClick={() => toggleSection('complement')}
              className="flex items-center justify-between w-full text-left"
            >
              <h2 className="text-xl font-semibold text-gray-800">4️⃣ Informações Extras (Opcional)</h2>
              {expandedSections.has('complement') ? (
                <ChevronUp className="w-5 h-5 text-gray-500" />
              ) : (
                <ChevronDown className="w-5 h-5 text-gray-500" />
              )}
            </button>
            
            {expandedSections.has('complement') && (
              <div className="mt-6 space-y-6">
                {renderFormField('Dificuldades Esperadas', 'difficulties', 'Quais obstáculos podem surgir?', false, 'textarea')}
                {renderFormField('Recursos Necessários', 'resources', 'Que recursos serão necessários?', false, 'textarea')}
                {renderFormField('Pontos de Atenção', 'attentionPoints', 'O que merece atenção especial?', false, 'textarea')}
              </div>
            )}
          </div>

          {/* Alertas */}
          {similarIdeas.length > 0 && (
            <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4">
              <div className="flex items-start gap-2">
                <AlertCircle className="w-5 h-5 text-yellow-600 mt-0.5" />
                <div>
                  <h3 className="font-medium text-yellow-800">💡 Ideias similares encontradas</h3>
                  <p className="text-sm text-yellow-700 mb-2">Sua ideia pode complementar estas:</p>
                  <ul className="text-sm text-yellow-700">
                    {similarIdeas.slice(0, 3).map((idea, index) => (
                      <li key={index}>• {idea}</li>
                    ))}
                  </ul>
                </div>
              </div>
            </div>
          )}

          {/* Botões de ação */}
          <div className="flex gap-4 pt-6 border-t">
            <button
              type="submit"
              className="flex items-center gap-2 px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors font-medium"
            >
              <Send className="w-4 h-4" />
              🚀 Enviar minha ideia
            </button>
            <button
              type="button"
              onClick={() => navigate('/dashboard')}
              className="flex items-center gap-2 px-6 py-3 bg-gray-300 text-gray-700 rounded-lg hover:bg-gray-400 transition-colors"
            >
              Cancelar
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default EnhancedIdeaForm;
